import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// icons/shadow
const markerIconBase64 =
  'data:image/png;base64,'; // Truncated for brevity
const markerShadowBase64 =
  'data:image/png;base64,'; // Truncated for brevity

const DefaultIcon = L.icon({
  iconUrl: markerIconBase64,
  shadowUrl: markerShadowBase64,
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  tooltipAnchor: [16, -28],
  shadowSize: [41, 41],
});

L.Marker.prototype.options.icon = DefaultIcon;

interface MapComponentProps {
  lat: number;
  lng: number;
}

const MapComponent: React.FC<MapComponentProps> = ({ lat, lng }) => {
  const mapContainerRef = useRef<HTMLDivElement>(null);
  const mapInstanceRef = useRef<L.Map | null>(null);
  const markerRef = useRef<L.Marker | null>(null);

  useEffect(() => {
    if (!mapContainerRef.current) return;

    if (!mapInstanceRef.current) {
      try {
        const map = L.map(mapContainerRef.current, {
          zoomControl: true,
          attributionControl: false,
          dragging: false,
          scrollWheelZoom: true,
          doubleClickZoom: true,
        }).setView([lat, lng], 13);

        L.tileLayer('https://tile.thunderforest.com/cycle/{z}/{x}/{y}.png?apikey=66942876bc2a47fe950ab69feb3d9395', {
          maxZoom: 19,
          subdomains: 'abc',
        }).addTo(map);

        const marker = L.marker([lat, lng]).addTo(map);

        mapInstanceRef.current = map;
        markerRef.current = marker;

        // Force a resize check in case container dimensions changed during render
        setTimeout(() => {
          map.invalidateSize();
        }, 100);
      } catch (e) {
        console.error('Error initializing map:', e);
      }
    } else {
      mapInstanceRef.current.setView([lat, lng], 13);
      if (markerRef.current) {
        markerRef.current.setLatLng([lat, lng]);
      }
    }

    return () => {};
  }, [lat, lng]);

  return (
    <div className="relative z-0 h-full w-full">
      <div
        ref={mapContainerRef}
        className="absolute inset-0 h-full min-h-[150px] w-full"
        style={{ backgroundColor: '#18181b' }}
      />
    </div>
  );
};

export default MapComponent;
